# Define the models we want to test
models = [
    "gpt-4o-mini",
    "o1-mini",
    "claude-3.5-sonnet",
    "gemini-pro-1.5"
]

# Create combinations of questions and models
combinations = []

# Get SEMrush metrics
semrush_metrics = {}
if step_2['output'] and len(step_2['output']) > 0:
    first_keyword = step_2['output'][0]
    semrush_metrics = {
        "volume": int(first_keyword.get('Search Volume', 0)),
        "cpc": float(first_keyword.get('CPC', '0')),
        "difficulty": int(first_keyword.get('Keyword Difficulty Index', 0))
    }
else:
    # Default metrics if no SEMrush data
    semrush_metrics = {
        "volume": 0,
        "cpc": 0,
        "difficulty": 0
    }

# Extract questions from step_10's output
step_10_output = step_10['output']

# Parse the markdown text to extract just the questions
import re

def extract_questions(text):
    # Match each non-empty line ending in a question mark
    question_pattern = r'^(.*\?)$'
    return re.findall(question_pattern, text, flags=re.MULTILINE)


# Extract the three questions
questions = extract_questions(step_10_output)

# Create combinations with clean structure
for question in questions:
    for model in models:
        combinations.append({
            "question": question,
            "model": model,
            "search_metrics": semrush_metrics
        })

return combinations

